mapper.insert:
		INSERT INTO users (
		merchant_id,
		EMAIL,
		PW,
		NAME,
		TEL,
		ADDR,
		CO_REG_NUM,
		MERCHANT_ID

		)
		VALUES(
		 #{email}
		, #{pw}
		, #{name}
		, #{tel}
		, #{addr}
		, #{co_reg_num}
		, #{merchant_id}

		)

mapper.update:
UPDATE users SET
PW = #{pw},
NAME = #{name},
TEL = #{tel}
WHERE EMAIL = #{email}

mapper.select:
SELECT COUNT(*) FROM users
WHERE
EMAIL = #{email}
